export const AddTask = (taskList, setTaskList) => {
    const handleChange = (event) => {
        event.preventDefault();
        const taskDate=new Date();

        console.log(taskDate.toString(36) + Math.random().toString(36).substr(2))

        console.log(taskDate.getTime());
        const obj={
            id: taskDate.toString(36) + Math.random().toString(36).substr(2),
            name: event.target.addTask.value,
            time: "23123234234234"
        }
        setTaskList=[...taskList,obj]
    }
  return (
    <section className="addTask">
        <form onSubmit={handleChange}>
            <input type="text" autoComplete="off" placeholder="Add Task" name="addTask" maxLength="25"/>
            <button type="submit">Add</button>
        </form>
    </section>
  )
}
